import React, { useEffect, useRef, useState } from "react";
import { Button, Input, Modal, Tooltip } from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  HomeOutlined,
  AppstoreAddOutlined,
  FileSearchOutlined,
} from "@ant-design/icons";
import { Table } from "antd";
import { useDispatch, useSelector } from "react-redux";
import Column from "antd/lib/table/Column";
import { useHistory } from "react-router-dom";
import {
  deleteLocationAction,
  getLocationDetailAction,
  getLocationListAction,
  updateImgLocationAction,
} from "../../../redux/Actions/locationAction";
import UploadLocationImg from "./UploadLocationImg/UploadLocationImg";

const { Search } = Input;

export default function AdminLocation() {
  const history = useHistory();
  const dispatch = useDispatch();
  const [selectedFile, setSelectedFile] = useState(null);

  const [imageUrl, setImageUrl] = useState("");

  const fileInput = useRef();
  const btnUpdateImg = useRef();

  const [page, setPage] = useState(1);
  useEffect(() => {
    dispatch(getLocationListAction());
  }, []);

  let { locationList } = useSelector((state) => state.locationReducer);

  const onChange = (pagination, filters, sorter, extra) => {
    // console.log("params", pagination, filters, sorter, extra);
  };

  const handleChangeFile = (e) => {
    //Lay file ra tu e
    let file = e.target.files[0];
    setSelectedFile(file);
    setIsModalVisible(true);

    let reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (e) => {
      setImageUrl(e.target.result);
    };
  };

  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleOk = () => {
    setIsModalVisible(false);
    btnUpdateImg.current.click();
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <Button
        onClick={() => {
          history.push("location/addnew");
        }}
        size="large"
        className="ml-5 mb-5 text-red-500 bg-white border-red-500 hover:bg-red-500 hover:text-white rounded"
      >
        Thêm vị trí mới
      </Button>

      <Table
        dataSource={locationList}
        onChange={onChange}
        rowKey={"_id"}
        pagination={{
          onChange(current) {
            setPage(current);
          },
        }}
      >
        <Column
          title="STT"
          key="stt"
          align="center"
          className="font-semibold"
          render={(value, item, index) => {
            return (page - 1) * 10 + index + 1;
          }}
        />
        <Column
          title="Edit"
          dataIndex="_id"
          key="edit"
          align="center"
          className="font-semibold"
          render={(id) => {
            return (
              <>
                <Tooltip color={"red"} title="Thêm phòng mới">
                  <button
                    onClick={() => {
                      history.push(`/admin/rooms/addnew/${id}`);
                    }}
                    className="text-yellow-600 text-2xl mr-4 cursor-pointer"
                  >
                    <AppstoreAddOutlined />
                  </button>
                </Tooltip>
                <Tooltip color={"red"} title="Xem danh sách các phòng">
                  <button
                    onClick={() => {
                      history.push(`/admin/rooms/${id}`);
                    }}
                    className="text-indigo-600 text-2xl mr-2 cursor-pointer"
                  >
                    <HomeOutlined />
                  </button>
                </Tooltip>
              </>
            );
          }}
        />
        <Column
          title="Name"
          dataIndex="name"
          align="center"
          className="font-semibold"
          key="name"
        />

        <Column
          title="Image"
          dataIndex="image"
          align="center"
          className="font-semibold"
          key="image"
          render={(img) => {
            return (
              <Tooltip color={"red"} title="Click để cập nhật ảnh của vị trí">
                <div
                  onClick={() => {
                    fileInput.current.click();
                  }}
                  className="flex justify-around items-center cursor-pointer"
                >
                  {img ? (
                    <img
                      style={{
                        width: 200,
                        height: 150,
                        objectFit: "cover",
                        borderRadius: 10,
                      }}
                      src={img}
                      alt={img.name}
                    />
                  ) : (
                    <img
                      style={{
                        width: 200,
                        height: 150,
                        objectFit: "cover",
                        borderRadius: 10,
                        border: "2px solid black",
                      }}
                      src="https://bitsofco.de/content/images/2018/12/broken-1.png"
                      alt="Ảnh của vị trí chưa được cập nhật"
                    />
                  )}
                </div>
              </Tooltip>
            );
          }}
        />

        <Column
          dataIndex="_id"
          key="image"
          align="center"
          className="font-semibold"
          render={(id) => {
            return (
              <div>
                <input
                  style={{ display: "none" }}
                  type="file"
                  onChange={handleChangeFile}
                  ref={fileInput}
                />
                {selectedFile ? (
                  <div>
                    <Modal
                      visible={isModalVisible}
                      onOk={handleOk}
                      onCancel={handleCancel}
                      width={700}
                      okType={"danger"}
                    >
                      <img
                        style={{
                          width: 600,
                          height: 400,
                          objectFit: "cover",
                          borderRadius: 14,
                        }}
                        src={imageUrl}
                        alt="..."
                      />
                    </Modal>
                    <Button
                      ref={btnUpdateImg}
                      style={{ display: "none" }}
                      onClick={() => {
                        const formdata = new FormData();
                        formdata.append(
                          "room",
                          selectedFile,
                          selectedFile?.name
                        );
                        setSelectedFile(null);
                        dispatch(updateImgLocationAction(formdata, id));
                      }}
                    >
                      Update
                    </Button>
                  </div>
                ) : (
                  <></>
                )}
              </div>
            );
          }}
        />
        <Column
          title="Province"
          align="center"
          className="font-semibold"
          dataIndex="province"
          key="province"
        />
        <Column
          title="Country"
          align="center"
          className="font-semibold"
          dataIndex="country"
          key="country"
        />
        <Column
          title="Valueate"
          align="center"
          className="font-semibold"
          dataIndex="valueate"
          key="valueate"
        />

        <Column
          title="Action"
          dataIndex="_id"
          key="action"
          align="center"
          className="font-semibold"
          render={(id) => {
            return (
              <>
                <Tooltip color={"red"} title="Xem chi tiết vị trí">
                  <button
                    onClick={() => {
                      dispatch(getLocationDetailAction(id));
                      history.push(`/admin/location/detail/${id}`);
                      // dispatch(getListTicketsByUserAction(id));
                    }}
                    className="text-green-600 text-2xl mr-4 cursor-pointer"
                  >
                    <FileSearchOutlined />
                  </button>
                </Tooltip>
                <Tooltip color={"red"} title="Sửa thông tin vị trí">
                  <button
                    onClick={() => {
                      dispatch(getLocationDetailAction(id));
                      history.push("/admin/location/edit");
                    }}
                    className="text-blue-600 text-2xl mr-4  cursor-pointer"
                  >
                    <EditOutlined />
                  </button>
                </Tooltip>
                <Tooltip color={"red"} title="Xóa vị trí">
                  <button
                    onClick={() => {
                      window.confirm(
                        "Bạn có chắc muốn xóa vị trí này không?"
                      ) && dispatch(deleteLocationAction(id));
                    }}
                    className="text-red-600 text-2xl cursor-pointer"
                  >
                    <DeleteOutlined />
                  </button>
                </Tooltip>
              </>
            );
          }}
        />
      </Table>
    </div>
  );
}
