import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import "./MyPofolio.css"
import axios from "axios";
import { DOMAIN, TOKEN_CYBERSOFT } from '../../../../configUrl/configURL';
import { Upload, Button } from "antd";

export default function MyPofolio() {
  let userInfor = useSelector((state) => state.userReducer.userInfor);
  const [antPics, setAntPics] = useState([]);
  const handleAnt = e => {
    console.log(e.file.originFileObj);
    setAntPics(e.file.originFileObj);
  };
  // useEffect(() => {
  //   axios({
  //     url: DOMAIN + `/api/users/upload-avatar`,
  //     method: "POST",
  //     headers: {
  //       tokenByClass: TOKEN_CYBERSOFT,
  //     },
  //   })
  //     .then((res) => {
  //       console.log("resAvatar", res);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // }, []);

  console.log("userInfor", { userInfor });
  return (
    <div className="flex justify-center flex-col mx-2">
      <div className="text-center relative py-5">
        <img src={userInfor.avatar} className="object-cover w-20 h-20 rounded-full mx-auto" />
        <div>
        <Upload onChange={handleAnt}>
        <input type="file" accept="image/x-png, image/gif, image/jpeg" style={{display: 'none'}} />
          <p className="cursor-pointer underline font-semibold mt-2">Update photo</p>
          </Upload>
          </div>
      </div>
      <div>
        <div className="border-b mb-4">
        <div>
          <svg stroke="currentColor" fill="currentColor" strokeWidth={0} viewBox="0 0 16 16" className="sc-1lmdwpx-6 fdGIua mx-auto" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg">
            <path fillRule="evenodd" d="M8 0c-.69 0-1.843.265-2.928.56-1.11.3-2.229.655-2.887.87a1.54 1.54 0 0 0-1.044 1.262c-.596 4.477.787 7.795 2.465 9.99a11.777 11.777 0 0 0 2.517 2.453c.386.273.744.482 1.048.625.28.132.581.24.829.24s.548-.108.829-.24a7.159 7.159 0 0 0 1.048-.625 11.775 11.775 0 0 0 2.517-2.453c1.678-2.195 3.061-5.513 2.465-9.99a1.541 1.541 0 0 0-1.044-1.263 62.467 62.467 0 0 0-2.887-.87C9.843.266 8.69 0 8 0zm2.146 5.146a.5.5 0 0 1 .708.708l-3 3a.5.5 0 0 1-.708 0l-1.5-1.5a.5.5 0 1 1 .708-.708L7.5 7.793l2.646-2.647z" />
          </svg>
        </div>
        <div className=" mb-6">
          <h1>Identity verification</h1>
          <p>Show others you're really you with the identity verification badge.</p>
          <div className="border pt-4 mx-8 rounded-lg cursor-pointer pofolio_button">
            <button>
              <p className="font-semibold text-lg">Get the badge</p>
              </button>
          </div>
        </div>
        </div>
        <div>
          <p>{userInfor.name} confirmed</p>
          <div className="flex flex-warp">
          <i className="fa-solid fa-check mt-1"></i>
          <p className="ml-1">Email address</p>
          </div>
          <div className="flex flex-warp">
          <i className="fa-solid fa-check mt-1"></i>
          <p className="ml-1">Phone number</p>
          </div>
        </div>
      </div>
    </div>
  )
}
